mapper.select:
SELECT tuy.c_integral_ratio FROM t_user tu LEFT JOIN t_user_type tuy ON
tu.type_id=tuy.id
WHERE tu.uid=#{uid};

mapper.update:
UPDATE `t_cagent_storedvalue`
SET usedvaue = usedvaue +#{amt}+#{cj}, remainvalue = remainvalue -#{amt}-#{cj}
WHERE cid = ( SELECT id FROM t_cagent WHERE t_cagent.cagent =#{cagent});

mapper.insert:
INSERT INTO t_user_treasure
(uid,amount,old_money,new_money,number,TYPE,t_type,ADDTIME)
VALUES (#{uid},#{amt},#{wallet},#{wallet}+#{amt},#{outTradeNo} ,'IN','存款', NOW());

INSERT INTO t_onlinepay_callback_log (params,ip,status,uptime) VALUES (#{params},INET_ATON(#{ip}),'success',NOW()); INSERT INTO t_user_treasure (uid,amount,old_money,new_money,number,type,t_type,addtime,cagent,ip) VALUES
(#{uid},#{cj},#{walletBalance}+#{orderAmount},#{walletBalance}+#{orderAmount}+#{cj},#{orderNo} ,'IN','彩金',NOW(),#{cagent},INET_ATON(#{ip})),
(#{uid},#{orderAmount},#{walletBalance},#{walletBalance}+#{orderAmount},#{orderNo} ,'IN','存款', NOW(),#{cagent},INET_ATON(#{ip}));
INSERT INTO t_cagent_storedvalue_log (cid,t_type,type,value,addtime) VALUES
(#{cid},'彩金','OUT',#{cj}, NOW()),
(#{cid},'存款','OUT',#{orderAmount}, NOW()); INSERT INTO t_user_quantity (cid,uid,marking_quantity,user_quantity,winAmount,user_winAmount) VALUES (#{cid},#{uid},#{dml}, 0,0,0) ON DUPLICATE KEY UPDATE marking_quantity = marking_quantity+#{dml}

mapper.select:
SELECT COUNT(1) FROM t_user_treasure 
WHERE uid=#{uid} AND number=#{orderNo} AND cagent=#{cagent} 
AND addtime BETWEEN DATE_FORMAT(NOW(),'%Y-%m-%d 00:00:00') AND NOW() ;

	SELECT 
  		id AS id,cid AS cid,pay_url AS payUrl,payment_name AS paymentName,payment_config AS paymentConfig
  	FROM t_cagent_ysepay WHERE id=#{payId}

	SELECT
  		id AS id,cid AS cid,pay_url AS payUrl,payment_name AS paymentName,payment_config AS paymentConfig
  	FROM t_cagent_ysepay WHERE cid = (SELECT id FROM t_cagent WHERE cagent = #{cagent}) AND payment_name = #{paymentName}

		SELECT
			IFNULL(dividend_rate,0) AS dividendRate,IFNULL(coding_rate,0) AS codingRate
		FROM t_user_channel
		WHERE 1=1 
		AND payment_id=#{payId} 
		AND cid=#{cid} 
		AND typeid=(select type_id from t_user where uid=#{uid}) 
		AND type =#{type}
        GROUP BY typeid

SELECT tuy.c_integral_ratio FROM t_user tu LEFT JOIN t_user_type tuy ON
tu.type_id=tuy.id
WHERE tu.uid=#{uid}
