mapper.select:
SELECT u.wallet,u.cagent,c.id cagentId,h.Handicap handicap
FROM t_user u
JOIN t_cagent c ON u.cagent=c.cagent
LEFT JOIN t_user_type_handicap h ON h.type_id = u.type_id AND game=#{platCode}
WHERE u.uid=#{uid}

SELECT
IFNULL(wallet,0.00) AS balance
FROM t_user WHERE uid=#{uid}

SELECT
IFNULL(wallet,0.00) AS balance
FROM t_user WHERE uid=#{uid}

SELECT ag AS AG, ag AS AGIN, bbin AS BBIN, bbin AS BBIN1, bbinGame AS BBIN2, cg AS CG, ds AS DS, ggby AS GGBY, haba AS HABA, hg AS HG, ig AS IG, ig AS IGLOTTERY, ig AS IGLOTTO, igpj AS IGPJ, igpj AS IGPJLOTTERY, igpj AS IGPJLOTTO, mgGame AS MG, aghsr AS AGBY, ob AS OB, og AS OG, pt AS PT, shenbo AS SB1, shenboGame AS SB2, bgVideo AS BG,
IF((shenbo=0 )OR(shenboGame=0) ,"0","1") AS SB,
VR as VR, yoplay as YOPLAY, tasspta as TASSPTA, vgqp as VG, gy as GY, nb as NB, ps as PS, kyqp as KYQP, lyqp as LYQP, jdb AS JDB, sw AS SW, ibc AS IBC, esw AS ESW, cqj AS CQJ FROM t_platform_status p WHERE p.cid = #{cid}


SELECT
	id, uid, billno, username, t_type as tType, t_money as tMoney, old_money as oldMoney,
	new_money as newMoney, type,t_time as tTime, ip,
result, cagent
FROM t_transfer 
WHERE uid=#{uid} 

AND type=#{type}


AND t_type=#{tType}

AND t_time BETWEEN #{bdate} AND #{edate}
ORDER BY t_time DESC LIMIT ${(pageNo-1)*pageSize},#{pageSize}


SELECT
	COUNT(1) cnt,
	SUM(CASE WHEN t_type='IN' THEN t_money ELSE -1*t_money END) total
FROM t_transfer 
WHERE uid=#{uid} 

AND type=#{type}


AND t_type=#{tType}

AND t_time BETWEEN #{bdate} AND #{edate}
