mapper.select:
SELECT COUNT(uid) FROM t_amount_record 
WHERE status IN (0) AND uid=#{uid} 
AND addtime BETWEEN DATE_FORMAT(now(),'%Y-%m-%d 00:00:00') AND DATE_FORMAT(now(),'%Y-%m-%d 23:59:59')

select * from ( select uid,order_time, case when pay_type=1 or pay_type=21 then '网银' when pay_type=3 or pay_type=23 then '微信' when pay_type=4 or pay_type=24 then '支付宝' when pay_type=5 or pay_type=25 then '财付通' when pay_type=7 or pay_type=27 then '银联扫码' when pay_type=6 or pay_type=26 then '京东支付' when pay_type=9 or pay_type=29 then '快捷支付' when pay_type=10 or pay_type=30 then '微信条码' when pay_type=11 or pay_type=31 then '支付宝条码' when pay_type=10 or pay_type=30 then '扫码支付' when pay_type=0 or pay_type=0 then '银行汇款' when pay_type=12 or pay_type=32 then '百度钱包' else '其他' end pay_type,order_amount, case trade_status when 'paying' then '处理中' when 'success' then '交易成功' else '交易失败' end trade_status,pay_type type,'无' As rmk from t_recharge where 1=1
AND uid = #{uid}
and DATE_FORMAT(order_time, '%Y-%m-%d') BETWEEN #{bdate} and #{edate}
union all select uid,addtime as order_time,'银行汇款' as pay_type,amount as order_amount,case `status` when '0' then '处理中' when '1' then '交易成功' else '交易失败' end trade_status,'4' as type,rmk as rmk from t_amount_record where 1=1
AND uid = #{uid}
and DATE_FORMAT(addtime, '%Y-%m-%d') BETWEEN #{bdate} and #{edate}
)a where 1=1
and pay_type = #{type}
and trade_status = #{status}
ORDER BY order_time DESC
